package com.java;

public class Ejercicio1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double var1=10;
		double var2=2;
		double var3=2; 
		
		double res;
		
		res= var1+var2+var3;
		
		System.out.println("Esta es la suma " + res);
		
		res= var1-var2-var3;
		System.out.println("Esta es la resta " + res);		
		
		res = var1*var2*var3;
		System.out.println("Esta es la multiplicacion " + res);
		
		res = var1/var2/var3;
		System.out.println("Esta es la division " + res);
	

	}

}
